import React, { useState, useEffect } from "react";
import Sidebar from "./Sidebar";
import Topbar from "./Topbar";
import baseUrl from '../../Api'

const Orderview = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    // Function to fetch all orders
    const fetchOrders = async () => {
      try {
        const response = await fetch(baseUrl + "/order/allorders");
        if (!response.ok) {
          throw new Error("Failed to fetch orders");
        }
        const data = await response.json();
        setOrders(data.ordersWithPetsAndUsers); // Set the fetched orders in state
      } catch (error) {
        console.error("Error fetching orders:", error);
      }
    };

    // Call the fetchOrders function when component mounts
    fetchOrders();
  }, []);

  return (
    <div>
      <Topbar />
      <Sidebar />
      <div>
        <h1>All Orders</h1>
        <table>
          <thead>
            <tr>
              <th>User Name</th>
              <th>Email</th>
              <th>Pet Name</th>
              <th>Order Status</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <tr key={order.orderId}>
                <td>{order.user ? order.user.username : "-"}</td>
                <td>{order.user ? order.user.email : "-"}</td>
                <td>{order.pet ? order.pet.PetName : "-"}</td>
                <td>{order.pet ? order.pet.status : "-"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Orderview;
