import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Card,Button , CardContent, Typography, InputLabel } from '@mui/material';
import NavBar from './Navbar';
import baseUrl from '../../Api';
import { Buffer } from 'buffer';

const Orders = () => {
  const [username, setUsername] = useState('');
  const [userId, setUserId] = useState('');
  const [orders, setOrders] = useState([]);
  const [items, setItems] = useState([]);
  const [showAddressInput, setShowAddressInput] = useState(false);
  const [address, setAddress] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const getToken = () => {
      return localStorage.getItem('token');
    };

    const token = getToken();
    if (!token) {
      navigate('/login');
    } else {
      const payload = token.split('.')[1];
      try {
        const decodedPayload = atob(payload);
        const { id: userId, username: decodedUsername } = JSON.parse(decodedPayload);

        setUsername(decodedUsername);
        setUserId(userId);

        // Fetch orders by userId
        const fetchOrders = async () => {
          try {
            const response = await axios.get(baseUrl + `/order/fetchorders/${userId}`);
            console.log('Orders response:', response.data.petsInOrders);
            setOrders(response.data.orders);
            setItems(response.data.petsInOrders);
          } catch (error) {
            console.error('Error fetching orders:', error);
          }
        };

        fetchOrders();
      } catch (error) {
        console.error('Error decoding token payload:', error);
      }
    }
  }, [navigate, userId]);

  const handleConfirmOrder = () => {
    setShowAddressInput(true);
  };

  const handleSaveAddress = () => {
    // Save the address and perform other necessary actions
    console.log('Address saved:', address);
    setShowAddressInput(false);
  };

  return (
    <div>
      <NavBar />
      <div style={{ width: '100%', marginTop: '20px', display: 'flex', justifyContent: 'center', flexWrap: 'wrap' }}>
        {items.map((pet) => (
          <Card key={pet._id} style={{ width: '400px', margin: '10px' }}>
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                Pet Name: {pet.PetName}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Breed: {pet.Breed}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Price: {pet.Price}
              </Typography>
              <div>
                <img
                  src={`data:${pet.Image.contentType};base64,${Buffer.from(pet.Image.data).toString('base64')}`}
                  alt="petImage"
                  style={{ height: '140px', width: 'auto', marginTop: '10px' }}
                />
              </div>
              {/* Show address input when confirm order button is clicked */}
              {showAddressInput && (
                <div>
                  <InputLabel>Enter Delivery Ad</InputLabel>
                  <input
                    type="text"
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    placeholder="Enter Address"
                    style={{ marginTop: '10px', width: '100%', padding: '5px' }}
                  />
                  <Button variant="contained" onClick={handleSaveAddress} style={{ marginTop: '10px' }}>
                    Save
                  </Button>
                </div>
              )}
              {!showAddressInput && (
                <Button variant="contained" onClick={handleConfirmOrder} style={{ marginTop: '10px' }}>
                  Confirm Order
                </Button>
              )}
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default Orders;
