import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Card, CardContent, Typography } from '@mui/material';
import NavBar from './Navbar';
import baseUrl from '../../Api';
import { Buffer } from 'buffer';

const Orders = () => {
  const [username, setUsername] = useState('');
  const [userId, setUserId] = useState('');
  const [orders, setOrders] = useState([]);
  const [items, setItems] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const getToken = () => {
      return localStorage.getItem('token');
    };

    const token = getToken();
    if (!token) {
      navigate('/login');
    } else {
      const payload = token.split('.')[1];
      try {
        const decodedPayload = atob(payload);
        const { id: userId, username: decodedUsername } = JSON.parse(decodedPayload);

        setUsername(decodedUsername);
        setUserId(userId);

        // Fetch orders by userId
        const fetchOrders = async () => {
          try {
            const response = await axios.get(baseUrl + `/order/fetchorders/${userId}`);
            console.log('Orders response:', response.data.petsInOrders);
            setOrders(response.data.orders);
            setItems(response.data.petsInOrders);
          } catch (error) {
            console.error('Error fetching orders:', error);
          }
        };

        fetchOrders();
      } catch (error) {
        console.error('Error decoding token payload:', error);
      }
    }
  }, [navigate, userId]);

  return (
    <div>
      <NavBar />
      <div style={{ width: '400px', marginTop: '20px' }}>
        <h2>{username}'s Orders</h2>
        <div className="order-items">
          {orders.map((order) => (
            <Card key={order._id} className="order-item" style={{ marginBottom: '20px' }}>
              <CardContent>
                <Typography gutterBottom variant="h5" component="div">
                  Order ID: {order._id}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Order Date: {new Date(order.orderDate).toLocaleDateString()}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Pet Details:
                </Typography>
                <ul>
                  {items && items.map((pet) => (
                    <li key={pet._id}>
                      <div>
                        {/* Assuming Image data is available and rendered correctly */}
                        <img
                          src={`data:${pet.Image.contentType};base64,${Buffer.from(pet.Image.data).toString('base64')}`}
                          alt="petImage"
                          style={{ height: '140px' }}
                        />
                      </div>
                      <div>
                        <Typography variant="body2">
                          Pet Name: {pet.PetName}
                        </Typography>
                        <Typography variant="body2">
                          Breed: {pet.Breed}
                        </Typography>
                        <Typography variant="body2">
                          Price: {pet.Price}
                        </Typography>
                      </div>
                    </li>
                  ))}
                </ul>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Orders;
