import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Card, CardContent, CardMedia, Typography, Button, Dialog, DialogTitle, DialogContent, DialogActions } from '@mui/material';
import NavBar from './Navbar';
import baseUrl from '../../Api';

const Orders = () => {
  const [username, setUsername] = useState('');
  const [userId, setUserId] = useState('');
  const [orders, setOrders] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const getToken = () => {
      return localStorage.getItem('token');
    };

    const token = getToken();
    if (!token) {
      navigate('/login');
    } else {
      const payload = token.split('.')[1];
      try {
        const decodedPayload = atob(payload);
        const { id: userId, username: decodedUsername } = JSON.parse(decodedPayload);

        setUsername(decodedUsername);
        setUserId(userId);

        // Fetch orders by userId
        const fetchOrders = async () => {
          try {
            const response = await axios.get(baseUrl + `/order/fetchorders/${userId}`);
            setOrders(response.data.orders);
          } catch (error) {
            console.error('Error fetching orders:', error);
          }
        };

        fetchOrders();
      } catch (error) {
        console.error('Error decoding token payload:', error);
      }
    }
  }, [navigate, userId]);

  return (
    <div>
      <NavBar />
      <div style={{ width: '400px', marginTop: '20px' }}>
        <h2>{username}'s Orders</h2>
        <div className="order-items">
          {orders.map((order) => (
            <Card key={order._id} className="order-item" style={{ marginBottom: '20px' }}>
              <CardMedia
                component="img"
                height="140"
                image={`data:${order.petsInOrders[0].Image.contentType};base64,${Buffer.from(order.petsInOrders[0].Image.data).toString('base64')}`}
                alt="petImage"
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="div">
                  {order.petsInOrders[0].PetName}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {order.petsInOrders[0].Breed}
                </Typography>
              </CardContent>
              <Typography variant="body2" color="text.secondary">
                Price: {order.petsInOrders[0].Price}
              </Typography>
              {/* You can add more details if needed */}
            </Card>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Orders;
